int HardDisk::GetCurrentPrice() const
{
    int ageInDays = GetAge();
    if(ageInDays < 30)
    {
        return initialPrice;
    }
    else if (ageInDays >= 30 && ageInDays <90)
    {return (int)(initialPrice * 0.9);}
    else
    {return (int)(initialPrice * 0.8);}
}
